import React from "react";
import { ThemeProvider } from "styled-components";
import ChatBot from "react-simple-chatbot";

const ChatbotWindow = ({ isVisible, onClose }) => {
    if (!isVisible) return null;

    const theme = {
        background: "#f5f8fb",
        headerBgColor: "#0084ff",
        headerFontColor: "#fff",
        botBubbleColor: "#0084ff",
        botFontColor: "#fff",
        userBubbleColor: "#fff",
        userFontColor: "#4a4a4a",
    };

    const steps = [
        { id: "1", message: "Olá! Como posso te ajudar?", trigger: "2" },
        { id: "2", user: true, trigger: "3" },
        { id: "3", message: "Obrigado pela mensagem! Estamos aqui para ajudar.", end: true },
    ];

    return (
        <div className="chatbot-container">
            <button className="close-button" onClick={onClose}>
                ✖
            </button>
            <ThemeProvider theme={theme}>
                <ChatBot steps={steps} />
            </ThemeProvider>
        </div>
    );
};

export default ChatbotWindow;
