import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import axios from 'axios';

// Estilos para o chatbot
const ChatbotContainer = styled.div`
  position: fixed;
  bottom: 80px;
  right: 20px;
  width: 350px;
  height: 500px;
  background: #fff;
  border-radius: 10px;
  box-shadow: 0 4px 10px rgba(0, 0, 0, 0.2);
  display: ${({ isVisible }) => (isVisible ? 'block' : 'none')};
  overflow: hidden;
`;

const ChatbotHeader = styled.div`
  background: #4a90e2;
  color: #fff;
  padding: 10px;
  font-size: 16px;
  text-align: center;
  border-top-left-radius: 10px;
  border-top-right-radius: 10px;
`;

const ChatbotMessages = styled.div`
  flex: 1;
  padding: 10px;
  overflow-y: auto;
  max-height: 380px;
  background: #f9f9f9;
`;

const ChatbotInputContainer = styled.div`
  display: flex;
  padding: 10px;
  border-top: 1px solid #ddd;
`;

const ChatbotInput = styled.input`
  flex: 1;
  padding: 10px;
  border: 1px solid #ddd;
  border-radius: 5px;
  margin-right: 10px;
`;

const ChatbotButton = styled.button`
  background: #4a90e2;
  color: #fff;
  padding: 10px 15px;
  border: none;
  border-radius: 5px;
  cursor: pointer;

  &:hover {
    background: #357abd;
  }
`;

const ChatbotToggleButton = styled.button`
  position: fixed;
  bottom: 20px;
  right: 20px;
  background: #4a90e2;
  color: #fff;
  padding: 15px;
  border: none;
  border-radius: 50%;
  box-shadow: 0 4px 10px rgba(0, 0, 0, 0.2);
  font-size: 20px;
  cursor: pointer;

  &:hover {
    background: #357abd;
  }
`;

const ChatbotWindow = () => {
    const [isOpen, setIsOpen] = useState(false);
    const [messages, setMessages] = useState([]);
    const [userMessage, setUserMessage] = useState('');

    // FunÃ§Ã£o para alternar a janela do chatbot
    const toggleChatbot = () => {
        setIsOpen(!isOpen);
    };

    // FunÃ§Ã£o para enviar a mensagem do usuÃ¡rio para a API
    const sendMessage = async () => {
        if (!userMessage.trim()) return;

        // Atualiza mensagens com a do usuÃ¡rio
        setMessages([...messages, { sender: 'user', text: userMessage }]);

        try {
            const response = await axios.post('AIzaSyDlDxmAL3i7CluPgZ8cbgz3xi9o8Hd76jk', {
                message: userMessage,
            });

            // Atualiza mensagens com a resposta da API
            setMessages((prev) => [
                ...prev,
                { sender: 'bot', text: response.data.reply },
            ]);
        } catch (error) {
            setMessages((prev) => [
                ...prev,
                { sender: 'bot', text: 'Erro ao obter resposta. Tente novamente.' },
            ]);
        }

        setUserMessage('');
    };

    return (
        <>
            <ChatbotToggleButton onClick={toggleChatbot}>
                ðŸ’¬
            </ChatbotToggleButton>

            <ChatbotContainer isVisible={isOpen}>
                <ChatbotHeader>Chatbot</ChatbotHeader>
                <ChatbotMessages>
                    {messages.map((message, index) => (
                        <div
                            key={index}
                            style={{
                                textAlign: message.sender === 'user' ? 'right' : 'left',
                                marginBottom: '10px',
                            }}
                        >
                            <strong>{message.sender === 'user' ? 'VocÃª' : 'Bot'}:</strong>{' '}
                            {message.text}
                        </div>
                    ))}
                </ChatbotMessages>
                <ChatbotInputContainer>
                    <ChatbotInput
                        type="text"
                        value={userMessage}
                        onChange={(e) => setUserMessage(e.target.value)}
                        placeholder="Digite sua mensagem..."
                    />
                    <ChatbotButton onClick={sendMessage}>Enviar</ChatbotButton>
                </ChatbotInputContainer>
            </ChatbotContainer>
        </>
    );
};

export default ChatbotWindow;
